    public void sort(int[] tmp, int[] sum, int len, int start, int end) {
        if (start == end) {
            return;
        }
        int pivot = findPivot(tmp, sum, start, end);
        int i = start, j = end;
        while (i <= j) {
            while (sum[tmp[i]] <= pivot) {
                i++;
            }
            while (sum[tmp[j]] >= pivot) {
                j--;
            }
            if (i <= j) {
                swap(tmp, i, j);
                swap(sum, i, j);
                i++;
                j--;
            }
        }
        sort(tmp, sum, len, start, i - 1);
        sort(tmp, sum, len, j + 1, end);
    }

    public int findPivot(int[] tmp, int[] sum, int start, int end) {
        int mid = start + (end - start) / 2;
        int p = sum[tmp[mid]];
        sort(tmp, sum, len, start, end);
        return p;
    }

    public void swap(int[] array, int index1, int index2) {
        int tmp = array[index1];
        array[index1] = array[index2];
