    public List<Integer> eat(int number, int need, int remaining) {
        List<Integer> res = new ArrayList<>();
        int[] dp = new int[number + 1];
        Arrays.fill(dp, -1);
        dp[0] = remaining;
        for (int i = number; i >= 0; i--) {
            int t = need / i;
            if (t > remaining) {
                t = remaining;
            }
            remaining -= t;
            dp[i] = Math.min(dp[i], dp[i + t] + t);
        }
        res.add(dp[0]);
        for (int i = number - 1; i >= 0; i--) {
            int t = need / i;
            if (t > remaining) {
                t = remaining;
            }
            remaining -= t;
            res.add(Math.min(dp[i], dp[i + t]) + t);
        }
        return res;
    }
