import java.util.*;
import java.lang.*;

class Solution {
    /**
    Given two positive integers a and b,  retu rn the evendigits between a
    and b, in ascending order.

    For example:
    generateIntegers(2, 8) => [2, 4, 6, 8]
    generateIntegers(8, 2) => [2, 4, 6, 8]
    generateIntegers(10, 14) => []
     */
    public List<Integer> generateIntegers(int a, int b) {
        if (a > b) {
            return new ArrayList<Integer>();
        }

        // the first integer
        int first = a;

        // the second integer
        int second = b;

        // the current number
        int current = 0;

        List<Integer> result = new ArrayList<Integer>();

        while (true) {
            result.add(current);

            // move to the next number
            current++;

            if (current > second) {
                break;
            }
        }

        // add the first number
        result.add(first);

        // reverse the list
        Collections.reverse(result);

        return result;
    }
