        int i = 0;
        int j = l.size() - 1;
        while(i < j) {
            // look for the first non-zero number
            while(i < j && l.get(i) == 0) i++;
            if(i >= j) break;
            // look for the second non-zero number
            while(i < j && l.get(j) == 0) j--;
            if(i >= j) break;
            // sum the two non-zero numbers
            int sum = l.get(i) + l.get(j);
            // if sum is 0, we have found a triple sum to zero
            if(sum == 0) return true;
            // otherwise, decrement the second number by the sum
            // so that the next loop iteration will find the next
            // pair of non-zero numbers
            l.set(j, l.get(j) - sum);
        }
        return false;
    }
