    public int carRaceCollision(int n) {
        int[][] table = new int[n][n];
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {
                table[i][j] = -1;
            }
        }
        table[0][0] = 0;
        for (int i = 0; i < n; i++) {
            for (int j = 1; j < n; j++) {
                table[i][j] = table[i][j - 1] + 1;
            }
        }
        for (int i = n - 2; i >= 0; i--) {
            for (int j = n - 1; j >= i + 1; j--) {
                table[i][j] = table[i][j - 1] + table[i + 1][j];
            }
        }
        return table[n - 1][n - 1];
    }
