    public List<Integer> common(List<Integer> l1, List<Integer> l2) {
        // sort the lists
        List<Integer> tmp = new ArrayList<Integer>(l1);
        Collections.sort(tmp);
        List<Integer> sorted = new ArrayList<Integer>(l2);
        Collections.sort(sorted);

        // check whether they are equal
        if (l1.size()!= l2.size())
            return new ArrayList<Integer>();

        int pos = 0;
        int i = 0;
        int j = 0;
        int m = tmp.size();
        int n = sorted.size();

        while (i < m && j < n) {
            int x = tmp.get(i);
            int y = sorted.get(j);

            if (x == y) {
                pos = i;
                break;
            }

            i++;
            j++;
        }

        // remove common elements
        List<Integer> ret = new ArrayList<Integer>();
        for (i = 0; i < m; i++) {
            if (i!= pos)
                ret.add(tmp.get(i));
        }

        return ret;
    }
