class Solution {
    /**
    Write a function that takes a string and returns true if the string
    length is a prime number or false otherwise
    Examples
    primeLength("Hello") == true
    primeLength("abcdcba") == true
    primeLength("kittens") == true
    primeLength("orange") == false
     */
    public boolean primeLength(String string) {
        int primeCount = 0;
        for (int i = 0; i < string.length(); i++) {
            if (i % 2 == 0) {
                continue;
            }
            int prime = 0;
            int number = 0;
            int temp = 0;
            while (number < string.length()) {
                temp = (int) (Math.pow(Integer.parseInt(string.substring(number, number + 1)), 2));
                if (temp > string.length()) {
                    break;
                }
                prime = (int) Math.sqrt(temp);
                number = prime;
            }
            if (prime == string.length()) {
                primeCount++;
            }
        }
        if (primeCount % 2 == 0) {
            return false;
        }
        return true;
    }
