import java.util.*;
import java.lang.*;

class Solution {
    /**
    Given a positive integer n, return the count of the numbers of n-digit
    positive integers that start or end with 1.
     */
    public int startsOneEnds(int n) {
        int count = 0;
        int k = 1;
        while (k * k <= n) {
            if (n % k == 0) {
                count++;
            }
            k++;
        }
        return count;
    }
}

