    public String matchParens(List<String> lst) {
        int size = lst.size();
        if (size == 0) {
            return "No";
        }
        String[] arr = new String[size];
        for (int i = 0; i < size; i++) {
            arr[i] = lst.get(i);
        }
        return matchParens(arr);
    }

    private String matchParens(String[] lst) {
        Stack<Character> stack = new Stack<Character>();
        for (int i = 0; i < lst.length; i++) {
            char c = lst[i].charAt(0);
            if (c == '(') {
                stack.push(c);
            } else if (c == ')') {
                if (stack.isEmpty()) {
                    return "No";
                }
                stack.pop();
            }
        }
        if (!stack.isEmpty()) {
            return "No";
        }
        return "Yes";
    }
