/* Find the shortest palindrome that begins with a supplied string.
  Algorithm idea is simple:
  - Find the longest postfix of supplied string that is a palindrome.
  - Append to the end of the string reverse of a string prefix that comes before the palindromic suffix.
  >>> makePalindrome('')
  ''
  >>> makePalindrome('cat')
  'catac'
  >>> makePalindrome('cata')
  'catac'
  */
const makePalindrome = (string) => {
  if (string == '')
    return '';
  var beginning_of_suffix = 0;
  var last_palindrome = 0;
  var suffix = '';
  var suffix_length = 0;
  while (string.length > beginning_of_suffix) {
    if (isPalindrome(string.substring(beginning_of_suffix, string.length))) {
      last_palindrome = beginning_of_suffix;
      suffix = string.substring(beginning_of_suffix, string.length);
      suffix_length = suffix.length;
      beginning_of_suffix = string.length;
    }
    beginning_of_suffix++;
  }
  return string.substring(0, last_palindrome + suffix_length) + suffix.split('').reverse().join('');
}

