  1
  >>> round(findPoly([-1, 0, 1, 0, 0, 1]), 2)
  1
  */
const findPoly = (xs, x) => {
  var coeffs = generateList(xs.length);
  while (coeffs.reduce((prev, item, index) => prev + item, 0) < x) {
    coeffs.splice(0, 1);
  }
  while (coeffs.reduce((prev, item, index) => prev + item, 0) > x) {
    coeffs.splice(coeffs.length - 1, 1);
  }
  return coeffs.reduce((prev, item, index) => prev + item * Math.pow(x, index), 0);
