// Question: Given a positive integer n, your task is to generate and return all the possible permutations of the digits.
//
// Example:
// For n = 1234, the output should be
// permute(1234) = 123, 4123, 123, 4123, 1234, 3412, 2341, 1423, 4343, 2341, 3142, 1324, 3214, 2341
// permute(54321) = 54321
//
// Solution: The solution is to use backtracking.
const generatePermutations = (num) => {
  if (num === 0) {
    return [''];
  }
  const permutations = [];
  let i;
  for (i = 0; i < num; i++) {
    const subPermutations = generatePermutations(num - 1);
    const subPermutation = subPermutations.map(p => p + nums[i]);
    permutations = permutations.concat(subPermutation);
  }
  return permutations;
};

