// Question: Given an array of digits, return all possible permutations.
//
// Example:
// For inputDigits = [1, 2, 3], the output should be
// permute(inputDigits) = [123, 132, 213, 231, 312, 321]
// permute(inputDigits) = [132, 213, 231, 321, 312, 123]
// permute(inputDigits) = [321, 312, 123, 132, 213, 231]
//
// Solution: The solution is to use backtracking.
const generatePermutations = (inputDigits) => {
  const permutations = [];
  let i;
  let j;
  let k;
  let result;
  let permutation;
  for (i = 0; i < inputDigits.length; i++) {
    result = [];
    for (j = 0; j < inputDigits.length; j++) {
      permutation = inputDigits.slice(0);
      permutation.splice(i, 1);
      for (k = 0; k < permutation.length; k++) {
        result.push(permutation[k]);
      }
    }
    permutations = permutations.concat(result);
  }
  return permutations;
};

