  const chars = 'abcdefghijklmnopqrstuvwxyz' + 'ABCDEFGHIJKLMNOPQRSTUVWXYZ' + '0123456789'
  const random = () => chars[Math.floor(Math.random() * (chars.length - 1))]
  return (length)? random().repeat(length) : random()
}

export const shuffle = (array) => {
  const length = array.length
  for (let i = length - 1; i > 0; i--) {
    const index = Math.floor(Math.random() * (i + 1))
    const temp = array[i]
    array[i] = array[index]
    array[index] = temp
  }
  return array
}

export const shuffleReverse = (array) => {
  const length = array.length
  for (let i = 0; i < length; i++) {
    const index = Math.floor(Math.random() * (length - i))
    const temp = array[i]
    array[i] = array[length - i - 1]
    array[length - i - 1] = temp
  }
  return array
}

export const flatten = (array) => {
  return [].concat(...array)
}

export const remove = (array, item) => {
