}

export const hasDuplicates = (array) => {
  let duplicates = false
  for (let i = 0; i < array.length - 1; i++) {
    for (let j = i + 1; j < array.length; j++) {
      if (array[i] === array[j]) {
        duplicates = true
        break
      }
    }
  }
  return duplicates
}

export const removeDuplicates = (array) => {
  let duplicates = false
  let temp = []
  for (let i = 0; i < array.length; i++) {
