export const reduce = (array, f, initial) => {
  let reduce = initial
  for (let i = 0; i < array.length; i++) {
    reduce = f(reduce, array[i])
  }
  return reduce
}

export const group = (array, f) => {
  let group = {}
  for (let i = 0; i < array.length; i++) {
    if (!group[f(array[i])]) {
      group[f(array[i])] = [array[i]]
    } else {
      group[f(array[i])].push(array[i])
    }
  }
  return group
}

