	*/
const combine = (strArr) => {
	if (strArr.length == 0) {
		return [];
	}
	let combinations = [];
	for (let i = 0; i < strArr.length; i++) {
		let substrings = strArr.slice(i).reduce((combination, string) => {
			if (combination.length > 0) {
				return combination.concat([string]);
			} else {
				return [string];
			}
		}, []);
		combinations = combinations.concat(substrings);
	}
	return combinations;
}

