const findLongest = (str) => {
	var longest = '';
	for (let i = 0; i < str.length; i++) {
		for (let j = i + 1; j < str.length; j++) {
			for (let k = j + 1; k < str.length; k++) {
				if (str.slice(i, j) + str.slice(j, k) + str.slice(k) == str) {
					longest = longest.concat(str.slice(i, j) + str.slice(j, k) + str.slice(k));
				}
			}
		}
	}
	return longest;
}

