		result.push(i);
	}
	return result;
};

const isNumber = (n) => {
	return typeof n === 'number';
};

const isInteger = (n) => {
	return n % 1 === 0;
};

const isOdd = (n) => {
	return n % 2!== 0;
};

const isEven = (n) => {
	return n % 2 === 0;
};

const hasOddNumber = (numbers) => {
	for (let i = 0; i < numbers.length; i++) {
		if (!isOdd(numbers[i])) {
			return false;
		}
	}
	return true;
};

const hasEvenNumber = (numbers) => {
	for (let i = 0; i < numbers.length; i++) {
		if (!isEven(numbers[i])) {
			return false;
		}
	}
	return true;
};

const hasOnlyEvenOrOdd = (numbers) => {
	for (let i = 0; i < numbers.length; i++) {
		if (!(isOdd(numbers[i]) || isEven(numbers[i]))) {
			return false;
		}
	}
	return true;
};

const findOdd = (numbers) => {
	for (let i = 0; i < numbers.length; i++) {
		if (!isOdd(numbers[i])) {
			return numbers[i];
		}
	}
};

const findEven = (numbers) => {
	for (let i = 0; i < numbers.length; i++) {
		if (!isEven(numbers[i])) {
			return numbers[i];
		}
	}
};

const removeDuplicates = (numbers) => {
	let output = [];
	for (let i = 0; i < numbers.length; i++) {
		if (output.indexOf(numbers[i]) === -1) {
			output.push(numbers[i]);
		}
	}
	return output;
};

const hasNoDuplicates = (numbers) => {
	return numbers.length === removeDuplicates(numbers).length;
};

const generateNumbers = (n) => {
	let numbers = generateIntegers(1, n);
	numbers.push(0);
	numbers.sort(function (a, b) {
		if (a === b) {
			return 0;
		} else if (a > b) {
			return 1;
		} else {
			return -1;
		}
	});
	return numbers;
};

const generateOddNumbers = (n) => {
	let numbers = generateNumbers(n);
	let even = findEven(numbers);
	if (even) {
		numbers.splice(numbers.indexOf(even), 1);
	}
	return numbers;
};

const generateEvenNumbers = (n) => {
	let numbers = generateNumbers(n);
	let odd = findOdd(numbers);
	if (odd) {
		numbers.splice(numbers.indexOf(odd), 1);
	}
	return numbers;
};

const generateNumbersWithEvenOrOdd = (n) => {
	let numbers = generateNumbers(n);
	numbers = removeDuplicates(numbers);
	if (hasOddNumber(numbers)) {
		numbers = generateOddNumbers(n);
	} else if (hasEvenNumber(numbers)) {
		numbers = generateEvenNumbers(n);
	}
	return numbers;
};

module.exports = {
	generateIntegers,
	generateNumbers,
	generateOddNumbers,
	generateEvenNumbers,
	generateNumbersWithEvenOrOdd,
	isNumber,
	isInteger,
	isOdd,
	isEven,
	hasOddNumber,
	hasEvenNumber,
	hasOnlyEvenOrOdd,
	findOdd,
	findEven,
	removeDuplicates,
	hasNoDuplicates
};
