      return collisions;
    }
    if(car1[car1pos] === car2[car2pos]) {
      collisions++;
      car1pos++;
      car2pos++;
    } else if(car1[car1pos] < car2[car2pos]) {
      car1pos++;
    } else if(car1[car1pos] > car2[car2pos]) {
      car2pos++;
    }
