/*
  What is the value of the first 1,000,000th to the 100,000th to the 10,000,000th
  to the 1,000,000th prime number?
  */
const findPrime = (n) => {
  let prime = 2;
  let i = 2;
  let p = 0;
  while(prime < n) {
    i++;
    while(i % prime!== 0) {
      prime++;
    }
    p++;
  }
  return p;
};

/*
  What is the value of the first 1,000,000th to the 100,000th to the 10,000,000th
  to the 1,000,000th prime number, then the value of the second prime number?
  */
const findPrime2 = (n) => {
  let p1 = findPrime(n);
  let p2 = 2;
  while(p2 < n) {
    p2++;
  }
  return p1 + p2;
};

