/* Input to this function is a string containing multiple groups of nested parentheses. Your goal is to
  separate those group into separate strings and return the list of those.
  Separate groups are balanced (each open brace is properly closed) and not nested within each other
  Ignore any spaces in the input string.
  >>> separateParenGroup('( ) (( )) (( )( ))')
  ['()', '(())', '(()())']
  */
const separateParenGroup = (paren_string) => {
  // Split the string into an array of substrings, one per parenthesis group.
  const groups = paren_string.split('(');
  const result = [];

  // For each group of parentheses, add all of the elements from that group.
  for (let i = 0; i < groups.length; i++) {
    const group = groups[i];

    // If there are parentheses within the group, split each pair of parentheses
    // into a string.
    if (group.length > 1) {
      const group_elements = group.split('');
      const first_element = group_elements.shift();

      // The first element must be an open parenthesis, so add it to the result.
      if (first_element === '(') {
        result.push(first_element);
      }

      // The remaining elements in the group are the elements within each parenthesis group.
      // Add them to the result.
      for (let j = 0; j < group_elements.length; j++) {
        if (group_elements[j] === '(') {
          // The element within the parentheses is an open parenthesis.
          // Add it to the result.
          result.push(group_elements[j]);
        } else if (group_elements[j] === ')') {
          // The element within the parentheses is a close parenthesis.
          // Find the corresponding open parenthesis and remove it from the result.
          const open_paren = group_elements.find((element) => element === '(');
          result.splice(result.indexOf(open_paren), 1);
        }
      }

      // If the group ended with a close parenthesis, add it to the result.
      if (group_elements.length === 0) {
        result.push(group_elements[0]);
      }
    } else {
      // If there are no parenthesis within the group, add the group to the result.
      result.push(group);
    }
  }

  return result;
};

