    digits.push(digit)
  }
  return digits
}

/*
  Given a positive integer n, return the number of trailing zeros in n.
  For example:
  trailingZeros(42) => 2
  trailingZeros(1) => 0
  trailingZeros(0) => 1
  */
const trailingZeros = (n) => {
  const zeros = 0
  const remainder = n % 10
  if (remainder === 0) {
    return zeros
  }
  return trailingZeros(remainder) + 1
}

/*
  Given two positive integers a and b, return the smallest positive integer
  that is larger than both a and b.

  For example:
  nextLarger(5, 8) => 10
  nextLarger(10, 5) => 15
  nextLarger(0, 9) => 1
  */
const nextLarger = (a, b) => {
  if (a < b) {
    return b
  }
  return nextLarger(a + 1, b + 1)
}

/*
  Given two positive integers a and b, return the largest positive integer
  that is smaller than both a and b.

  For example:
  nextSmaller(5, 8) => 5
  nextSmaller(10, 5) => 5
  nextSmaller(0, 9) => 9
  */
const nextSmaller = (a, b) => {
  if (a < b) {
    return a
  }
  return nextSmaller(a - 1, b - 1)
}

