const median = (l) => {
  let length = l.length;
  if (length === 0) {
    return NaN;
  }

  if (length === 1) {
    return l[0];
  }

  const half = length / 2;
  let sorted_l = l.slice(0);
  sorted_l.sort((a, b) => a - b);

  if (length % 2 === 1) {
    return sorted_l[half];
  } else {
