const gotCollatz = (n) => {
  let result = []
  let cur = n
  while (cur > 1) {
    if (cur % 2 === 0) {
      cur = cur / 2
    } else {
      cur = 3 * cur + 1
    }
    result.push(cur)
  }
  return result
}

