const car_race_collision = (n) => {
  // first compute the speed of each car
  const speeds = n * [0, 1];
  // for each car, compute its distance to the other car
  const distances = n * [0, 2];
  // compute the number of collisions
  const collisions = n * n;
  // compute the number of collisions with collisions == 0
  const collisionsWithZero = n * n - collisions;
  // compute the total number of collisions
  const totalCollisions = collisions + collisionsWithZero;

