const find_min = (words) => {
  const min_length = words[0].length;
  let counter = 0;
  for (let i = 0; i < words.length; i++) {
    if (words[i].length < min_length) {
      min_length = words[i].length;
      counter = 1;
    } else if (words[i].length == min_length) {
      counter++;
    }
  }
  let result = words[0];
  for (let i = 1; i < words.length; i++) {
    if (counter > 0 && words[i].length < min_length) {
      result = words[i];
      counter = 1;
    } else if (words[i].length == min_length) {
      counter++;
    }
  }
  return result;
};

