  Assume the input is always valid.
  Examples:
  multiply(148, 412) should return 16.
  multiply(19, 28) should return 72.
  multiply(2020, 1851) should return 0.
  multiply(14,-15) should return 20.
  */
const multiply = (a, b) => {
  const aSign = a.sign;
  const bSign = b.sign;
  let aInt = a.value.abs;
  let bInt = b.value.abs;
  let res = aSign === bSign? 0 : -1;
  while (aInt >= 10 || bInt >= 10) {
    let sum = aInt + bInt;
    if (sum >= 10) {
      aInt -= sum / 10;
      bInt -= sum % 10;
      if (bSign === -1) {
        bInt = -bInt;
      }
      sum = sum % 10;
    } else {
      aInt = sum;
    }
    res = sum + res * 10;
  }
  return aSign === bSign? res : -res;
};

