/**
  * Finds the smallest number in given list of numbers
  *
  * @param {Array} numbers
  * @returns {number}
  *
  * @example
  *
  *   find_smallest_number([1, 2, 3])
  *   // => 1
  *
  *   find_smallest_number([1, 2, 3, 4])
  *   // => 1
  *
  *   find_smallest_number([1, 2, 3, 4, 5])
  *   // => 1
  */
const find_smallest_number = (numbers) => {
  return (
    numbers.reduce((acc, x) => {
      if (acc < x) {
        return x
      }
    })
  )
}

/**
  * Finds the largest number in given list of numbers
  *
  * @param {Array} numbers
  * @returns {number}
  *
  * @example
  *
  *   find_largest_number([1, 2, 3])
  *   // => 3
  *
  *   find_largest_number([1, 2, 3, 4])
  *   // => 4
  *
  *   find_largest_number([1, 2, 3, 4, 5])
  *   // => 5
  */
const find_largest_number = (numbers) => {
  return (
    numbers.reduce((acc, x) => {
      if (acc > x) {
        return x
      }
    })
  )
}

/**
  * Calculate the sum of all numbers in given array.
  *
  * @param {Array} numbers
  * @returns {number}
  *
  * @example
  *
  *   sum_of_numbers([1, 2, 3])
  *   // => 6
  */
const sum_of_numbers = (numbers) => {
  return (
    numbers.reduce((acc, x) => acc + x, 0)
  )
}

