  * Calculate the mean of all numbers in given array.
  *
  * @param {Array} numbers
  * @returns {number}
  *
  * @example
  *
  *   mean_of_numbers([1, 2, 3])
  *   // => 2
  */
const mean_of_numbers = (numbers) => {
  return (
    sum_of_numbers(numbers) / numbers.length
  )
