      dp[j] = row + grid[j][i];
      row = next;
    }

    paths.push(dp[n - 1]);
  }

  const sorted_paths = paths.sort((a, b) => a - b);
  return k <= sorted_paths.length? sorted_paths.slice(0, k) : sorted_paths;
};

