  >>> same_chars_rev('eabcdzzzz', 'dddzzzzzzzddddabc')
  false
  */
const same_chars_rev = (s0, s1) => {
    if (s0.length!== s1.length) return false;
    for (let i = 0; i < s0.length; i++) {
        if (s0[i]!== s1[s0.length - i - 1]) return false;
    }
