  >>> same_chars_any('eabcdzzzz', 'abcd')
  false
  >>> same_chars_any('eabcdzzzz', 'dddddddabc')
  false
  >>> same_chars_any('abcd', 'dddddddabc')
  false
  >>> same_chars_any('eabcd', 'dddddddabce')
  false
  */
const same_chars_any = (s0, s1) => {
    if (s0.length!== s1.length) return false;
    for (let i = 0; i < s0.length; i++) {
        if (same_chars(s0.slice(0, i + 1), s1.slice(0, i + 1))) {
