const roderByPoints = (nums) => {
  if (!Array.isArray(nums)) {
    return [];
  }

  const n = nums.length;
  if (n === 0) {
    return [];
  }

  const sorted = nums.slice(0);
  sorted.sort((a, b) => {
    const diff = a - b;
    if (diff === 0) {
      return a > b? -1 : 1;
    }
    return diff;
  });

  return sorted;
};

