/*
  Given a string text, replace all spaces in it with underscores, 
  and if a string has more than 2 consecutive spaces, 
  then replace all consecutive spaces with - 

  fixSpacse("Example") == "Example"
  fixSpacse("Example 1") == "Example_1"
  fixSpacse(" Example 2") == "_Example_2"
  fixSpacse(" Example   3") == "_Example-3"
  */
const fixSpacse = (text) => {
  let output = text.split(" ")
  output = output.map(text => {
    if (text.length > 2) {
      let last = text.substring(text.length - 1)
      if (last === " ") {
        output.splice(output.length - 1, 1)
      }
      if (text.substring(0, 2) === "  ") {
        return "-" + text.substring(2)
      } else {
        return text.substring(0, text.length - 1)
      }
    } else {
      return text
    }
  })
  return output.join(" ")
}

/*
  Given a string text, replace all newlines with newline-space,
  and if a string has more than 2 consecutive newlines,
  then replace all consecutive newlines with -
*/
const fixNewlines = (text) => {
  let output = text.split("\n")
  output = output.map(text => {
    if (text.length > 2) {
      let last = text.substring(text.length - 1)
      if (last === "\n") {
        output.splice(output.length - 1, 1)
      }
      if (text.substring(0, 2) === "\n\n") {
        return "--" + text.substring(2)
      } else {
        return text.substring(0, text.length - 1)
      }
    } else {
      return text
    }
  })
  return output.join("\n")
}

const format = (text, options) => {
  options = options || {}
  text = fixSpacse(text)
  text = fixNewlines(text)
  let output = ""
  let newline = true
  for (let line of text.split("\n")) {
    if (newline) {
      output += line + "\n"
    } else {
      output += "   " + line + "\n"
    }
    newline =!newline
  }
  return output
}

