  for (let char of string) {
    let upper = char.toUpperCase()
    if (counts[upper] === undefined) {
      counts[upper] = 1
    } else {
      counts[upper]++
    }
    let lower = char.toLowerCase()
    if (counts[lower] === undefined) {
      counts[lower] = 1
    } else {
      counts[lower]++
    }
  }
  let result = 0
  for (let [key, value] of Object.entries(counts)) {
    result += value
  }
  return result
}

