/*
  Imagine a road that's a perfectly straight infinitely long line.
  n cars are driving left to right;  simultaneously, a different set of n cars
  are driving right to left.   The two sets of cars start out being very far from
  each other.  All cars move in the same speed.  Two cars are said to collide
  when a car that's moving left to right hits a car that's moving right to left.
  However, the cars are infinitely sturdy and strong; as a result, they continue moving
  in their trajectory as if they did not collide.

  This function outputs the number of such collisions.
  */
const carRaceCollision = (n) => {
    const cars = new Array(n);
    let counter = 0;
    for (let i = 0; i < n; i++) {
        cars[i] = {
            left: {x: 0, y: 0},
            right: {x: 0, y: 0}
        };
    }
    cars[0].left.x = 1;
    cars[0].left.y = -1;
    cars[0].right.x = -1;
    cars[0].right.y = -1;

    cars[n - 1].left.x = -1;
    cars[n - 1].left.y = 1;
    cars[n - 1].right.x = 1;
    cars[n - 1].right.y = 1;

    let next = 0;
    let prev = 0;
    let move = 1;
    let step = 1;

    while (true) {
        cars[next].x += cars[prev].x * move;
        cars[next].y += cars[prev].y * move;
        cars[next].x = Math.round(cars[next].x);
        cars[next].y = Math.round(cars[next].y);
        if (cars[next].x >= n) {
            cars[next].x = 0;
            cars[next].y = -1;
            cars[prev].x = 0;
            cars[prev].y = 1;
            move = -1;
            step++;
            if (step > n) {
                return counter;
            }
        } else if (cars[next].x <= -1) {
            cars[next].x = n - 1;
            cars[next].y = 1;
            cars[prev].x = n - 1;
            cars[prev].y = -1;
            move = -1;
            step++;
            if (step > n) {
                return counter;
            }
        }
        next++;
        if (next === n) {
            next = 0;
        }
        prev = next;
    }
};

