/*
  Imagine a road that's a perfectly straight infinitely long line.
  n cars are driving left to right;  simultaneously, a different set of n cars
  are driving right to left.   The two sets of cars start out being very far from
  each other.  All cars move in the same speed.  Two cars are said to collide
  when a car that's moving left to right hits a car that's moving right to left.
  However, the cars are infinitely sturdy and strong; as a result, they continue moving
  in their trajectory as if they did not collide.

  This function outputs the number of such collisions.
  */
const carRaceCollision = (n) => {
  let carsLeftToRight = [];
  let carsRightToLeft = [];

  for (let i = 0; i < n; i++) {
    carsLeftToRight.push(new Car({
      speed: 1,
      direction: 'left',
      startingPosition: {
        x: Math.random(),
        y: Math.random()
      },
      maxSpeed: 1,
      maxTurn: 360
    }));

    carsRightToLeft.push(new Car({
      speed: 1,
      direction: 'right',
      startingPosition: {
        x: Math.random(),
        y: Math.random()
      },
      maxSpeed: 1,
      maxTurn: 360
    }));
  }

  let collisions = 0;
  carsLeftToRight.forEach(car => {
    carsRightToLeft.forEach(collidingCar => {
      if (car.collidesWith(collidingCar)) {
        collisions++;
      }
    });
  });

  return collisions;
};

const carsOnTheRoad = (n) => {
  let cars = [];

  for (let i = 0; i < n; i++) {
    cars.push(new Car({
      speed: 1,
      direction: 'left',
      startingPosition: {
        x: Math.random(),
        y: Math.random()
      },
      maxSpeed: 1,
      maxTurn: 360
    }));
  }

  cars.forEach(car => {
    cars.forEach(collidingCar => {
      if (car.collidesWith(collidingCar)) {
        car.speed = 0;
      }
    });
  });

  return cars;
};

const printCar = (car) => {
  console.log(car.toString());
};

const carRace = (n) => {
  return new Promise((resolve, reject) => {
    console.log(`${n} cars are on the road.`);

    carsOnTheRoad(n).forEach(car => {
      car.collisions = carRaceCollision(n);
      resolve(car);
    });
  });
};

const carRacePromise = (n) => {
  return new Promise((resolve, reject) => {
    console.log(`${n} cars are on the road.`);

    carRace(n).then(cars => {
      cars.forEach(car => {
        printCar(car);
      });
      resolve(cars);
    }).catch(err => {
      reject(err);
    });
  });
};

module.exports = {
