        if (stack[i] === 0) {
            stack.splice(i, 1);
            i--;
        } else if (stack[i] === 1) {
            stack.splice(i, 1);
            i--;
        } else if (stack[i] === 2) {
            stack.splice(i, 1);
            i--;
        } else if (stack[i] > 3) {
            stack.splice(i, 1);
            i--;
        }
    }
    for (let i = stack.length - 1; i >= 0; i--) {
        maxLen = Math.max(maxLen, stack[i]);
    }
