    for (let i = 0; i < string.length - 1; i++) {
        for (let j = i + 1; j < string.length; j++) {
            if (string[i] === string[j]) {
                if (string[i].length === string[j].length) {
                    repeated = true;
                    result = [i, j];
                    break;
                }
            }
        }
