const sortNumbers = (numbers) => {
  let sortedNumbers = [];
  numbers.split(' ').forEach(num => {
    if (num.length === 1) {
      sortedNumbers.push(num);
    } else {
      const number = parseInt(num, 10);
      const index = sortedNumbers.indexOf(num);
      if (index === -1) {
        sortedNumbers.push(num);
      } else {
        sortedNumbers[index] = (sortedNumbers[index] < number)? sortedNumbers[index] : number;
      }
    }
  });
  return sortedNumbers.join(' ');
};

