  * If n is not prime then return prime number that is less than n
  *
  * @example
  * prime(8)
  * // => 8
  * prime(25)
  * // => 23
  * prime(70)
  * // => 59
  */
const prime = (n) => {
  let result = n
  while (result < n) {
    if (n % result === 0) {
      return result
    } else {
      result++
    }
  }
  return result
}

