  let closestDistance = Infinity;

  // Calculate the closest distance between each number in the list
  numbers.forEach((number) => {
    const distance = Math.abs(number - closestPair[1]);

    if (distance < closestDistance) {
      closestPair = [number, closestPair[1]];
      closestDistance = distance;
    }
  });

  return closestPair;
};

const getDirection = (x1, y1, x2, y2) => {
  // Get the slope of the line formed by the two points
  const slope = (y2 - y1) / (x2 - x1);

  // If the slope is less than 0, then the line is going down (negative slope)
  if (slope < 0) {
    // Calculate the slope at the y-intercept of the line
    const intercept = y1 - slope * x1;

    // If the intercept is less than the x-coordinate of the first point, then the line is
    // going left (negative slope)
    if (intercept < x1) {
      return "LEFT";
    }

    // If the intercept is greater than the x-coordinate of the second point, then the line is
    // going right (positive slope)
    if (intercept > x2) {
      return "RIGHT";
    }
  }

  // If the slope is greater than 0, then the line is going up (positive slope)
  if (slope > 0) {
    // Calculate the slope at the y-intercept of the line
    const intercept = y1 + slope * x1;

    // If the intercept is less than the x-coordinate of the first point, then the line is
    // going left (negative slope)
    if (intercept < x1) {
      return "LEFT";
    }

    // If the intercept is greater than the x-coordinate of the second point, then the line is
    // going right (positive slope)
    if (intercept > x2) {
      return "RIGHT";
    }
  }

  // The slope is equal to zero
  return "NONE";
};

const compareDirection = (a, b) => {
  if (a == "LEFT" && b == "RIGHT") {
    return -1;
  }

  if (a == "RIGHT" && b == "LEFT") {
    return 1;
  }

  if (a == "LEFT" && b == "LEFT") {
    return 0;
  }

  if (a == "RIGHT" && b == "RIGHT") {
    return 0;
  }

  return 0;
};

const findLongest = (list) => {
  let longest = 0;
  list.forEach((item) => {
    if (item.length > longest) {
      longest = item.length;
    }
  });

  return longest;
};

module.exports = {
  findClosestElements,
  getDirection,
  findLongest,
  compareDirection
