const mergeSort = (arr) => {
  if (arr.length === 1) {
    return arr
  }

  let mid = Math.floor(arr.length / 2)
  let left = mergeSort(arr.slice(0, mid))
  let right = mergeSort(arr.slice(mid, arr.length))

  return merge(left, right)
}

const merge = (left, right) => {
  let leftLen = left.length
  let rightLen = right.length
  let result = []

  for (let i = 0; i < leftLen + rightLen; i++) {
    if (i >= leftLen) {
      result.push(right[i - leftLen])
    } else if (i >= rightLen) {
      result.push(left[i - rightLen])
    } else {
      if (left[i] < right[i]) {
        result.push(left[i])
      } else {
        result.push(right[i])
      }
    }
  }

  return result
}

module.exports = {
  strangeSortList,
  mergeSort
