  if (k > n * n) {
    return []
  }
  const dp = new Array(n * n + 1)
  dp[0] = 1
  for (let i = 1; i <= n * n; i++) {
    dp[i] = Infinity
    for (let j = 0; j < n * n; j++) {
      if (i - j > 0 && grid[j][i - j]!== undefined) {
        dp[i] = Math.min(dp[i], dp[j] + 1)
      }
    }
  }
  const minPaths = []
  for (let i = 1; i <= n * n; i++) {
    if (dp[i] === k) {
      minPaths.push(grid[k - 1][i - 1])
    }
  }
  return minPaths
}

