  false
  >>> sameChars('eabcdzzzz', 'dddzzzzzzzddddabc')
  false
  */
const sameChars = (s0, s1) => {
    const [char0, char1] = [s0.charAt(0), s1.charAt(0)];
    const [count0, count1] = [0, 0];
    const [index0, index1] = [0, 0];
    const indexLimit = s0.length;
    while (index0 < indexLimit && index1 < indexLimit) {
        if (char0!== char1) break;
        index0++;
        index1++;
        count0++;
        count1++;
    }
    return count0 === count1 && index0 === index1;
};
