  for (let i = 1; i <= n; i++) {
    for (let j = i + 1; j <= n; j++) {
      for (let k = j + 1; k <= n; k++) {
        if (i === j && j === k) continue;
        if (i < j && j < k) continue;
        result.push((i * i - i + 1) * (j * j - j + 1) * (k * k - k + 1));
      }
    }
  }
  return result.length;
};

