const selectWords = (s, n) => {
  const result = []
  let word = ''
  for (let i = 0; i < s.length; i++) {
    let c = s[i]
    if (i === n - 1) {
      result.push(word)
      word = ''
    }
    else if (i >= n - 1) {
      word += c
      if (word.includes(' ')) {
