const fixSpaces = (text) => {
  if(text.match(/\s{2,}/)) {
    return text.replace(/\s{2,}/g, '-')
  }
  return text
}

const getHtml = (text) => {
  let lines = text.split(/\r\n|\r|\n/g)
  let lineNumber = 1
  let html = ""
  let lineHtml = ""

  lines.forEach((line) => {
    lineHtml = ""
    if(line.match(/\s{2,}/)) {
      lineHtml = lineHtml + "<br>"
      line = line.replace(/\s{2,}/g,'')
    }
    lineHtml = lineHtml + line + "<br>"
    html = html + lineHtml
    lineNumber = lineNumber + 1
  })

  return html
}

module.exports = {
  getHtml: getHtml,
  fixSpaces: fixSpaces
}
