  const len = l.length;
  let count = 0;
  const sum = l.reduce((acc, elem, index) => {
    if (acc === 0) {
      if (index === len - 1) {
        return elem;
      } else {
        return elem + l[index + 1];
      }
    } else {
      return elem + acc;
    }
  }, 0);
  for (i = 0; i < len - 2; i += 1) {
    for (j = i + 1; j < len - 1; j += 1) {
      for (k = j + 1; k < len; k += 1) {
        if (l[i] + l[j] + l[k] === 0) {
          count += 1;
        }
      }
    }
  }
  return count === 3;
};

